mapper.select:
SELECT info.company_name as companyName, topindex.industry_topindex_name as industryTopIndexName, substring(info.company_addr,1,2) as companyAddr, (SELECT COUNT(*) FROM tb_company_review review WHERE review.company_cd = info.company_cd) as reviewCount, (SELECT COUNT(*) FROM tb_company_interview interview WHERE interview.company_cd = info.company_cd) as interviewCount, (SELECT COUNT(*) FROM tb_company_salary salary WHERE salary.company_cd = info.company_cd) as salaryCount, IFNULL(info.company_total_rate/(SELECT COUNT(*) FROM tb_company_review review WHERE review.company_cd=info.company_cd AND review.review_allow=1),0) as companyTotalRate FROM tb_company_info info, tb_industry_topindex topindex WHERE topindex.industry_topindex_cd = (SELECT industry_topindex_cd FROM tb_industry_midindex WHERE industry_midindex_cd = info.industry_midindex_cd)
AND info.company_name LIKE CONCAT('%', #{searchCompanyName}, '%')
ORDER BY companyTotalRate DESC LIMIT #{pageHelp.startRow}, #{pageHelp.linePerPage}

SELECT review.company_review_cd as companyReviewCd, review.company_name as companyName, (SELECT job_topindex_name FROM tb_job_topindex WHERE job_topindex_cd=review.job_topindex_cd) as jobTopIndexName, review.review_comment as reviewComment, review.review_strength as reviewStrength, review.review_weak as reviewWeak, DATE_FORMAT(review.review_regdate, '%Y-%m-%d %H:%i') as reviewRegDate FROM tb_company_review review WHERE review.review_allow = 1
AND review.job_topindex_cd = #{jobTopIndexCd}
AND review.company_name LIKE CONCAT('%', #{searchCompanyName}, '%')
ORDER BY review.review_regdate DESC LIMIT #{pageHelp.startRow}, #{pageHelp.linePerPage}

SELECT COUNT(*) FROM tb_company_review WHERE review_allow = 1
AND job_topindex_cd = #{jobTopIndexCd}
AND company_name LIKE CONCAT('%', #{searchCompanyName}, '%')
